import classes from './Calculator.module.css';
import { Form, FormGroup, Label,Button } from "reactstrap";
import { useState } from 'react';
const UserProfile = () => {
  const [enteredNumber, setenteredNumber] = useState([]);
  const changeExpressionHandler = (evt)=>{
setenteredNumber(evt.target.value);
console.log(Object.assign([], evt.target.value))

  }
const evaluateExpressionHandler =(event)=>{
  event.preventDefault();
  console.log(typeof enteredNumber);
  const xxx = [...enteredNumber];
  console.log(xxx);
  console.log(typeof xxx);
  let tasks = []
tasks.push(enteredNumber);
console.log(JSON.parse(tasks))
 const x =evalRpn(JSON.parse(tasks));
 console.log(x);
  //evalRPN(enteredNumber);
  
}
const evalRpn =(A)=>
{
 
    // Initialize the stack
    let st = [];
     
    // Iterating to the each character
    // in the array of the string
    for (let i = 0; i < A.length; i++)
    {
     
        // If the character is not the special character
        // ('+', '-' ,'*' , '/')
        // then push the character to the stack
        if (A[i] !== "+" && A[i] !== "-" && A[i] !== "/"
            && A[i] !== "*") {
            st.push(parseFloat(A[i]));
            continue;
        }
       
         // else if the character is the special
         // character then use them to
         // perform the action
        else {
            let b = parseFloat(st.pop());
            let a = parseFloat(st.pop());
            console.log(b);
            console.log(a);
            if (A[i] === "+")
                st.push(a + b);
            else if (A[i] === "-")
                st.push(a - b);
            else if (A[i] === "*")
                st.push(a * b);
            else
                st.push(parseInt(a / b));
        }
    }
  
    return parseInt(st[st.length-1]);
}


  
  return (
    <main className={classes.profile}>
      <h2>My Calculator</h2>
      <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
                Enter a number or an expression:
              </Label>
              <input
             className={classes.input}
                style={{ fontSize: "12px" }}
                placeholder="Enter a number or expression"
                 value={enteredNumber}
                onChange={changeExpressionHandler}
                // onBlur={this.checkEmail}
              />
              
            </FormGroup>
           <br/>
            <Button onClick={evaluateExpressionHandler}>Calculate</Button>
            

    </main>
  );
};

export default UserProfile;
