import classes from './Calculator.module.css';
import { Form, FormGroup, Label,Button } from "reactstrap";
import { useState } from 'react';
const UserProfile = () => {
  const [enteredNumber, setenteredNumber] = useState([]);
const evaluateExpressionHandler =(event)=>{
  event.preventDefault();
  console.log(typeof enteredNumber);
 
  //evalRPN(enteredNumber);
  
}
function cuttingString(list) {
  // Allow multiple spaces, also at start/end:
  let adjustArr = list.match(/\S+/g); 
  let topper = [];
  // Use for..of loop
  for (let token of adjustArr) {
      if (!isNaN(token)) {
          // Why not convert to number here...
          //   and allow decimals. Use unary plus
          topper.push(+token);
      } else if (topper.length < 2) {
          // Show error message when not enough arguments
          throw "not enough arguments for " + token;
      } else {
          let j = topper.pop();
          let k = topper.pop();
          if (token === '+') {
              topper.push(j + k);
          }
      }
  }
  // Verify that the evaluation is complete
  if (topper.length !== 1) {
      throw "Evaluation did not yield one value but " + topper.length;
  }
  // Return the value 
  console.log(topper)
  return topper.pop();
}
var evalRPN = function(tokens) {
  let set = new Set();
  set.add('+');
  set.add('-');
  set.add('/');
  set.add('*');
  console.log(set);
  let i = 0;
  console.log(tokens.length)
  while(tokens.length > 1) {
    console.log(tokens[i+2])
      if(set.has(tokens[i+2])) {
          const result = calculate(parseInt(tokens[i]), parseInt(tokens[i+1]), tokens[i+2]);
          console.log(result);
          tokens.splice(i, 3, result);
          i = 0;
      } else
          i++;
  }
  
  return tokens[0];
};
  
const calculate = (left, right, operator) => {
  let res;
  switch(operator) {
      case '+':
          res = left + right;
          break;
      case '-':
          res = left - right;
          break;
      case '*':
          res = left * right;
          break;
      case '/':
          res = left / right;
          break;
  }
return res;
}
  
  return (
    <main className={classes.profile}>
      <h2>My Calculator</h2>
      <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
                Enter a number or an expression:
              </Label>
              <input
             className={classes.input}
                style={{ fontSize: "12px" }}
                placeholder="Enter a number or expression"
                 value={enteredNumber}
                onChange={(evt)=>{setenteredNumber(evt.target.value)}}
                // onBlur={this.checkEmail}
              />
              
            </FormGroup>
           <br/>
            <Button onClick={evaluateExpressionHandler}>Calculate</Button>
            

    </main>
  );
};

export default UserProfile;
