import classes from './Calculator.module.css';
import { FormGroup, Label,Button } from "reactstrap";
import { useState } from 'react';
import {refreshIcon} from '../assets/img/refresh-icon.png'
const UserProfile = () => {
  const [enteredNumber, setenteredNumber] = useState([]);
  const [resultValue, setresultValue] = useState('');
  const changeExpressionHandler = (evt)=>{
setenteredNumber(evt.target.value);
  }
const evaluateExpressionHandler =(event)=>{
  event.preventDefault();
  
  let tasks = []
tasks.push(enteredNumber);
 const x =evalRpn(JSON.parse(tasks));
 setresultValue(x);
}
const evalRpn =(A)=>
{
    let st = [];
   
    for (let i = 0; i < A.length; i++)
    {
     
        
        if (A[i] !== "+" && A[i] !== "-" && A[i] !== "/"
            && A[i] !== "*") {
            st.push(parseFloat(A[i]));
            continue;
        }
       
       
        else {
            let b = parseFloat(st.pop());
            let a = parseFloat(st.pop());
            if (A[i] === "+")
                st.push(a + b);
            else if (A[i] === "-")
                st.push(a - b);
            else if (A[i] === "*")
                st.push(a * b);
            else
                st.push(parseInt(a / b));
        }
    }
  
    return parseFloat(st[st.length-1]).toFixed(1);
}


  
  return (
    <main className={classes.profile}>
      <h2>My Calculator</h2>
      <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
                Enter a number or an expression:
              </Label>
              <input
             className={classes.input}
                style={{ fontSize: "12px" }}
                placeholder="Enter a number or expression"
                 value={enteredNumber}
                onChange={changeExpressionHandler}
                // onBlur={this.checkEmail}
              />
              
            </FormGroup>

           <br/>
          {resultValue !== '' &&  <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
              The result of the above expression is:
              </Label>
             <h3>{resultValue}</h3>
              
            </FormGroup>}
            <Button onClick={evaluateExpressionHandler}>Calculate</Button>
            <Button
                    id="tableButtons"
                    className="btn-round btn-icon fourthStepTour"
                    color="primary"
                    
                  >
                   <img src={refreshIcon} />
                  </Button>

    </main>
  );
};

export default UserProfile;
