import classes from './Calculator.module.css';
import { FormGroup, Label,Button } from "reactstrap";
import { useState } from 'react';
import refreshIcon from '../assets/img/refresh-icon.png';
import CalculatorImage from '../assets/img/calculator image.png';
const UserProfile = () => {
  const [enteredNumber, setenteredNumber] = useState([]);
  const [resultValue, setresultValue] = useState('');
  const changeExpressionHandler = (evt)=>{
    console.log(evt.target.value);
setenteredNumber(evt.target.value);
  }
const evaluateExpressionHandler =(event)=>{

  event.preventDefault();

  let tasks = []
  console.log(typeof enteredNumber);

try {
  tasks.push(enteredNumber);
} catch (error) {
  console.log(error)
}
 const x =evalRpn(JSON.parse(tasks));
 setresultValue(x);
}
const reloadAppHandler = ()=>{
  setenteredNumber([]);
  setresultValue('');
}
const evalRpn =(A)=>
{
  if(typeof A === 'number'){
    setresultValue(A)
  }
  console.log(A)
  // if(A.length!==1){
  //   setresultValue('please enter a valid reverse polish notation')
  // }
    let st = [];
   
    for (const element of A)
    {
     
        
        if (element !== "+" && element !== "-" && element !== "/"
            && element !== "*") {
            st.push(parseFloat(element));
            
        }
       
       
        else {
            let b = parseFloat(st.pop());
            let a = parseFloat(st.pop());
            if (element === "+")
                st.push(a + b);
            else if (element === "-")
                st.push(a - b);
            else if (element === "*")
                st.push(a * b);
            else
                st.push(parseInt(a / b));
        }
    }
    console.log(st.length);
  if(st.length === 0){
    console.log(st);
    alert ("boom");
  }
    return parseFloat(st[st.length-1]).toFixed(1);
}


  
  return (
    <main className={classes.profile}>
      <h2>My Calculator</h2>
      
      <img src={CalculatorImage} height='80px' width='80px'/>
      <br/>
      <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
                Enter an arithmetic expression in a Reverse Polish Notation:
              </Label>
              <input
             className={classes.input}
                style={{ fontSize: "12px" }}
                placeholder="Enter an arithmetic expression in a Reverse Polish Notation"
                 value={enteredNumber}
                onChange={changeExpressionHandler}
               
              />
              
            </FormGroup>

           <br/>
          {resultValue !== '' &&  <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
              The result of the above expression is:
              </Label>
             <h3>{resultValue}</h3>
              
            </FormGroup>}
            <div style={{display:'flex',marginLeft:'250px'}}>

            
            <Button onClick={evaluateExpressionHandler}>Calculate</Button>
            &nbsp;&nbsp;
           
                   <img src={refreshIcon} alt='refreshIcon' width='50px' height='50px' onClick={reloadAppHandler} title='reload calculator'/>
                   </div>      

    </main>
  );
};

export default UserProfile;
