import classes from './Calculator.module.css';
import { Form, FormGroup, Label,Button } from "reactstrap";
import { useState } from 'react';
const UserProfile = () => {
  const [enteredNumber, setenteredNumber] = useState([]);
const evaluateExpressionHandler =(event)=>{
  event.preventDefault();
  console.log(typeof enteredNumber);
 evalRpn(enteredNumber);
  //evalRPN(enteredNumber);
  
}
const evalRpn =(A)=>
{
 
    // Initialize the stack
    let st = [];
     
    // Iterating to the each character
    // in the array of the string
    for (let i = 0; i < A.length; i++)
    {
     
        // If the character is not the special character
        // ('+', '-' ,'*' , '/')
        // then push the character to the stack
        if (A[i] !== "+" && A[i] !== "-" && A[i] !== "/"
            && A[i] !== "*") {
              console.log(A[i])
            //st.push(parseInt(A[i]));
            //console.log(st);
            continue;
          
        }
       
         // else if the character is the special
         // character then use them to
         // perform the action
        else {
            let b = parseInt(st.pop());
            let a = parseInt(st.pop());
            if (A[i] == "+")
                st.push(a + b);
            else if (A[i] == "-")
                st.push(a - b);
            else if (A[i] == "*")
                st.push(a * b);
            else
                st.push(parseInt(a / b));
        }
    }
    console.log(parseInt(st[st.length-1]))
    return parseInt(st[st.length-1]);
}

var evalRPN = function(tokens) {
  let set = new Set();
  set.add('+');
  set.add('-');
  set.add('/');
  set.add('*');
  console.log(set);
  let i = 0;
  console.log(tokens.length)
  while(tokens.length > 1) {
    console.log(tokens[i+2])
      if(set.has(tokens[i+2])) {
          const result = calculate(parseInt(tokens[i]), parseInt(tokens[i+1]), tokens[i+2]);
          console.log(result);
          tokens.splice(i, 3, result);
          i = 0;
      } else
          i++;
  }
  
  return tokens[0];
};
  
const calculate = (left, right, operator) => {
  let res;
  switch(operator) {
      case '+':
          res = left + right;
          break;
      case '-':
          res = left - right;
          break;
      case '*':
          res = left * right;
          break;
      case '/':
          res = left / right;
          break;
  }
return res;
}
  
  return (
    <main className={classes.profile}>
      <h2>My Calculator</h2>
      <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
                Enter a number or an expression:
              </Label>
              <input
             className={classes.input}
                style={{ fontSize: "12px" }}
                placeholder="Enter a number or expression"
                 value={enteredNumber}
                onChange={(evt)=>{setenteredNumber(evt.target.value)}}
                // onBlur={this.checkEmail}
              />
              
            </FormGroup>
           <br/>
            <Button onClick={evaluateExpressionHandler}>Calculate</Button>
            

    </main>
  );
};

export default UserProfile;
