import classes from './Calculator.module.css';
import { Form, FormGroup, Label,Button } from "reactstrap";
import { useState } from 'react';
const UserProfile = () => {
  const [enteredNumber, setenteredNumber] = useState([]);
const evaluateExpressionHandler =(event)=>{
  event.preventDefault();
  console.log(enteredNumber);
  eveluateRPN(enteredNumber);
}
const eveluateRPN=(enteredNumber)=>{
  let stack = [];
  console.log(enteredNumber.length)
  for (let i = 0; i < enteredNumber.length; i++)
    {
      console.log(enteredNumber[i])
     
        // If the character is not the special character
        // ('+', '-' ,'*' , '/')
        // then push the character to the stack
        if (enteredNumber[i] !== `"+"` && enteredNumber[i] !== `"-"` && enteredNumber[i] !== `"/"`
            && enteredNumber[i] !== `"*"`) {
            stack.push(parseInt(enteredNumber[i]));
          
        }
         
         // else if the character is the special
         // character then use them to
         // perform the action
        else {
         
            let b = parseInt(stack.pop());
            let a = parseInt(stack.pop());
            console.log(b);
            console.log(a);
            if (enteredNumber[i] === "+")
                stack.push(a + b);
            else if (enteredNumber[i] === "-")
                stack.push(a - b);
            else if (enteredNumber[i] === "*")
                stack.push(a * b);
            else
                stack.push(parseInt(a / b));
        }
    }
    console.log(stack)
    console.log(parseInt(stack[stack.length-1])) ;

}
  
  return (
    <main className={classes.profile}>
      <h2>My Calculator</h2>
      <FormGroup>
              <Label
                for="exampleEmail"
                style={{ fontWeight: "600", size: "14px" }}
              >
                Enter a number or an expression:
              </Label>
              <input
             className={classes.input}
                style={{ fontSize: "12px" }}
                placeholder="Enter a number or expression"
                 value={enteredNumber}
                onChange={(evt)=>{setenteredNumber(evt.target.value)}}
                // onBlur={this.checkEmail}
              />
              
            </FormGroup>
           <br/>
            <Button onClick={evaluateExpressionHandler}>Calculate</Button>
            

    </main>
  );
};

export default UserProfile;
